import React, { useState, useEffect } from "react";
import Person from "./components/Person";
import personService from './services/notes'

const Phonebook = () => {
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [showAll, setShowAll] = useState(true);
  const [newFilter, setNewFilter] = useState("");
  {/* 
  const peopleToShow = showAll
    ? persons
    : persons.filter(person => person.name === null);
    useEffect(() => {
    console.log('effect')
    axios
    .get('http://localhost:3001/persons')
    .then(response => {
      console.log(response)
      setPersons(response.data)
    })
  }, [])
  console.log('render', persons.length)
*/}


  useEffect(() => {
    console.log('effect')
    personService
      .getAll()
      .then(initialContacts => {
        setPersons(initialContacts)
      })
  }, [])
  console.log('render', persons.length)


  const addPerson = event => {
    event.preventDefault();
    const personObject = {
      name: newName,
      phone: newPhone,
      id: newPhone
    };
    if (persons.findIndex(person => person.name === newName) > -1) {
      console.log("Person already exists");
      alert(`${newName} is already added to phonebook`);
    } else {
    personService 
      .create(personObject)
      .then(response => {
        setPersons(persons.concat(response.data))
        setNewName('')
        setNewPhone('')
      })
    }
  };

  const changePhone = id => {
    const person = persons.find(p => p.id === id)
    const changed = { ...person, phone: newPhone }

    personService 
      .update(id, changed)
      .then(returnPerson => {
        setPersons(persons.map(person => person.id !== id ? person : returnPerson))
      })
      .catch(error => {
        alert(`Phone was already deleted`)
      setPersons(persons.filter(p => p.id !== id))
    })
  }


  const filterChangeHandler = event => {
    console.log(event.target.value);
    setNewFilter(event.target.value);
  };

  const rows = () =>
    persons
      .filter(person =>
        person.name.toLowerCase().includes(newFilter.toLowerCase())
      )
      .map(person => <Person key={person.phone} person={person} changePhone={() => changePhone(person.id)}/>);

  const nameChangeHandler = event => {
    console.log(event.target.value);
    setNewName(event.target.value);
  };
  const phoneChangeHandler = event => {
    console.log(event.target.value);
    setNewPhone(event.target.value);
  };

  return (
    <div>
      <input value={newFilter} onChange={filterChangeHandler} />
      <button onClick={() => setShowAll(!showAll)}>
        show {showAll ? "important" : "all"}
      </button>
      <h1> Phonebook </h1>
      <h1> Add New Contact </h1>

      <form onSubmit={addPerson}>
        Name:
        <input value={newName} onChange={nameChangeHandler} />
        <br />
        Phone:
        <input value={newPhone} onChange={phoneChangeHandler} />
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h1>Numbers </h1>
      <div>{rows()}</div>
    </div>
  );
};

export default Phonebook;
