import React from "react";
const Person = ({ person, changePhone }) => {
  const label = person.phone 
  ? 'change' : 'changed'

  return (
    <div>
      <li>
        {" "}
        {person.name} {person.phone}{" "}
        <button onClick={changePhone}> {label} </button>
      </li>
    </div>
  );
};

export default Person;
